import styles from './BurgerIngredients.module.css'
import React from 'react'
import {Tab}  from "@ya.praktikum/react-developer-burger-ui-components"


export default function BurgerIngredients({ data }) {

    function ComponetBlock (list, name) {
      const component = list.filter(element => element.type === name);
      return createComponent(component)
    }

    function createComponent(list) {
        list.forEach(e => {


        const component = {
            name : e.name,
            price: e.price
        };
        });
        return component; 
    }

    const buns = ComponetBlock(data, 'bun');

    //const buns = data.filter(element => element.type === 'bun');
    const main = data.filter( el => el.type == 'main' );
    const sauce = data.filter( el => el.type == 'sauce' );

    console.log(buns)
    const [current, setCurrent] = React.useState('one')

    return (
    <section className="mt-5 mr-10" style={{ display: 'flex', flexDirection: 'column'}}>
        <h1 className="text text_type_main-large ml-10">Соберите бургер</h1>
        <div className="mt-5 ml-10" style={{ display: 'flex'}}>
        <Tab value="one" active={current === 'one'} onClick={setCurrent}>
            Булки
        </Tab>
        <Tab value="two" active={current === 'two'} onClick={setCurrent}>
            Соусы
        </Tab>
        <Tab value="three" active={current === 'three'} onClick={setCurrent}>
            Начинки
        </Tab>
      </div>
      <div>
        <ul style={{ listStyle: 'none'}}>
            <li className="text text_type_main-medium">Булки</li>
            <li className="text text_type_main-medium">Соусы</li>
            <li className="text text_type_main-medium">Начинки</li>
        </ul>
      </div>
      </section>
    )
}
