import styles from './IngredientDetails.module.css'


const ingredientCompositionArray = [
    {
       name: 'Калории,ккал',
       value: 'calories'
    },
    {
       name: 'Белки, г',
       value: 'proteins',
    },
    {
       name: 'Жиры, г',
       value: 'fat',
    },
    {
        name: 'Углеводы, г',
        value: 'fat',
     }
 ]

function IngredientDetailNutrition({ 

}) {
 return (
    <div className={styles.ingredient__nutrition_element}>  
    <p className="text text_type_main-default text_color_inactive">Калории,ккал</p>
    <p  className="text text_type_main-default text_color_inactive">{ingredient.calories}</p>
  </div>
 )
}

export default function IngredientDetails({ingredient, isModal}) {
    console.log(isModal)

    return (
    <section className={styles.ingredient_details__modal}>
        <h2 className="text text_type_main-large mt-10 ml-10">Детали ингредиента</h2>
        <img src ={ingredient.image} style={{alignSelf:"center", width:"480px", height:"240px", backgroundCover:'no-repeat', backgroundSize:'cover'}}/>
        <p className="text text_type_main-medium mt-4" src ={ingredient.image} style={{alignSelf:"center"}}>{ingredient.name}</p>
        <div className={styles.ingredient__nutrition}>
          <div className={styles.ingredient__nutrition_element}>  
            <p className="text text_type_main-default text_color_inactive">Калории,ккал</p>
            <p  className="text text_type_main-default text_color_inactive">{ingredient.calories}</p>
          </div>
          <div className={styles.ingredient__nutrition_element}>  
            <p className="text text_type_main-default text_color_inactive">Белки, г</p>
            <p  className="text text_type_main-default text_color_inactive">{ingredient.proteins}</p>
          </div>
          <div className={styles.ingredient__nutrition_element}>  
            <p className="text text_type_main-default text_color_inactive">Жиры, г</p>
            <p  className="text text_type_main-default text_color_inactive">{ingredient.fat}</p>
          </div>
          <div className={styles.ingredient__nutrition_element}>  
            <p className="text text_type_main-default text_color_inactive">Углеводы, г</p>
            <p  className="text text_type_main-default text_color_inactive">{ingredient.carbohydrates}</p>
          </div>
        </div>
      </section>
    )
}
