import styles from './BurgerIngredients.module.css'
import { CurrencyIcon, Counter } from "@ya.praktikum/react-developer-burger-ui-components";
import React from 'react'
import { useContext, useEffect, useState } from "react";
import {Tab}  from "@ya.praktikum/react-developer-burger-ui-components"
import IngredientDetails from "../Modals/IngredientDetails/IngredientDetails"


const ingredientBlockNames = [
    {
       name: 'Булки',
       value: 'bun',
    },
    {
       name: 'Соусы',
       value: 'sauce',
    },
    {
       name: 'Начинки',
       value: 'main',
    }
 ]

function IngredientCard(ingredient) {
    const ingridientElement = ingredient.ingredient
    const [modal, setStateModal] = React.useState('none');

    console.log(modal)
    return(
        <>
          <section  style={{ display:"flex", flexDirection:"column",  width:"272px", height: "208px", alignItems:'center', position:"relative"}}>
          <Counter count={1} size="default" extraClass="m-1"/>
            <img src={ingridientElement.image} style={{ width: "240px" , height: "120px"}}/>
            <div className="mt-2 " style={{ display:"flex", alignItems:"center", gap:"9px"}}>
              <p className="text text_type_digits-default mt-2 " >{ingridientElement.price}</p>
              <CurrencyIcon type="primary"  />
            </div>
            <p className='text text_type_main-small mt-2' style={{textAlign: "center"}}>{ingridientElement.name}</p>
          </section>
          <IngredientDetails ingredient={ingridientElement}/>
       </>
    )
}

 function IngridientsBlock({ 
    ingredientBlockName,
    ingredientsArray
    }) {
    const ingredientGroupArray = ingredientsArray.filter(element => element.type === ingredientBlockName.value);

     return (
        <section className="mt-10">
          <li id = {ingredientBlockName.value} className="text text_type_main-medium">{ingredientBlockName.name}</li>
          <div className={styles.grid}>

          {ingredientGroupArray.map( groupIngredient =>
            <IngredientCard 
            key={groupIngredient.name}
            ingredient={groupIngredient}></IngredientCard>
        )}
          </div>
        </section> 
     )
 }


export default function BurgerIngredients({ data }) {

    const [currentBlock, setBlockState] = React.useState('bun')

    React.useEffect(() => {
        document.getElementById(currentBlock).scrollIntoView({ behavior: "smooth" } )
      }, [currentBlock]);

    return (
    <section className="mt-10" style={{ display: 'flex', flexDirection: 'column'}}>
        <h1 className="text text_type_main-large ml-10">Соберите бургер</h1>
        <div className="mt-5 ml-10" style={{ display: 'flex'}}>
        {ingredientBlockNames.map( tab =>

            <Tab
                key={ tab.name }
                value={ tab.value }
                active={ currentBlock === tab.value }
                onClick={ setBlockState }
            >{tab.name }
            </Tab>
            )
            }
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', overflowY: "scroll", height: '756px'}}>
        <ul style={{ listStyle: 'none'}}>
        {ingredientBlockNames.map( ingredientName =>
            <IngridientsBlock 
            key={ingredientName.name}
            ingredientBlockName={ingredientName}
            ingredientsArray={data}></IngridientsBlock>
        )}
        </ul>
      </div>
      </section>
    )
}
