import React from 'react'


const modalRoot = document.getElementById("modal");

export default function Modal(isModalOpened) {
    const [modal, isModalOpen] = React.useState(false);

    const toggleModal = () => {
      isModalOpen(!modal);
    };
    return(
        <>
          <section  onClick = {toggleModal} style={{ display:"flex", flexDirection:"column",  width:"272px", height: "208px", alignItems:'center', position:"relative"}}>
          <Counter count={1} size="default" extraClass="m-1"/>
            <img src={ingridientElement.image} style={{ width: "240px" , height: "120px"}}/>
            <div className="mt-2 " style={{ display:"flex", alignItems:"center", gap:"9px"}}>
              <p className="text text_type_digits-default mt-2" >{ingridientElement.price}</p>
              <CurrencyIcon type="primary"  />
            </div>
            <p className='text text_type_main-small mt-2' style={{textAlign: "center"}}>{ingridientElement.name}</p>
          </section>
          { modal && <IngredientDetails ingredient={ingridientElement} isModalOpened = {modal} />}
       </>
    )
}
