import styles from './BurgerConstructorBlock.module.css';
import { ConstructorElement, DragIcon} from "@ya.praktikum/react-developer-burger-ui-components";

export default function BurgerConstructorBlock({
  text,
  price,
  thumbnail,
  type,
  isLocked
  }) {
   return (
    <>
    <li  className={styles.ingredient__column}>
      <div>
            { !type && <DragIcon type="primary"/> }
      </div >
      <div className={styles.ingredient__element}>
      <ConstructorElement 
            type={type}
            isLocked={isLocked}
            text={text}
            price={price}
            thumbnail={thumbnail}
          />
        </div>
    </li>
    </>
   )
}
