import styles from './IngredientDetails.module.css'



function IngredientDetailNutrition({ ingredient 
}) {

    const ingredientCompositionArray = [
        {'Калории,ккал': ingredient.calories},
        { 'Белки, г': ingredient.proteins}, 
        { 'Жиры, г': ingredient.fat},
        { 'Углеводы, г': ingredient.fat}
     ] 

   return (

    <div className={styles.ingredient__nutrition}>
        {ingredientCompositionArray.map( name =>
            <div className={styles.ingredient__nutrition_element}>  
            <p className="text text_type_main-default text_color_inactive">{name.key}</p>
            <p  className="text text_type_main-default text_color_inactive">{name.value}</p>
           </div>
        )}
   </div>
 )
}

export default function IngredientDetails({ingredient}) {

    return (
    <section className={styles.ingredient_details__modal}>
        <h2 className="text text_type_main-large mt-10 ml-10">Детали ингредиента</h2>
        <img src ={ingredient.image} style={{alignSelf:"center", width:"480px", height:"240px", backgroundCover:'no-repeat', backgroundSize:'cover'}}/>
        <p className="text text_type_main-medium mt-4" src ={ingredient.image} style={{alignSelf:"center"}}>{ingredient.name}</p>
        <IngredientDetailNutrition ingredient={ingredient}/>
      </section>
    )
}
