import styles from './IngredientDetails.module.css'



export default function IngredientDetails({ingredient}) {
    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
      }
    const orderNumber = getRandomInt(999999);
    return (
    <section className={styles.ingredient_details__modal}>
        <h2 className="text text_type_main-medium mt-10 ml-10">Детали ингредиента</h2>
        <img className="mt-15" src ={ingredient.image} style={{alignSelf:"center", width:"480px", height:"240px", backgroundCover:'no-repeat', backgroundSize:'cover'}}/>
        <p className="text text_type_main-medium mt-8">{ingredient.name}</p>
      </section>
    )
}
