import styles from './BurgerConstructor.module.css';
import { Button, ConstructorElement, CurrencyIcon, DragIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import BurgerConstructorBlock from '../BurgerConstructorBlock/BurgerConstructorBlock'
import BurgerTotalBlock from '../BurgerTotalBlock'



export default function BurgerConstructor( { data }) {

  const burgerFilling = data.filter(element => element.type === 'main');
  const burgerbun  = data.filter(element => element.type === 'bun');

  console.log(burgerbun)
    return (
        <section className="mt-20" style={{display: 'flex', flexDirection: 'column', gap: '16px',  height: '696px', width:'600px'}}>
            {burgerbun.length>1 && <BurgerConstructorBlock
            key = {burgerbun[0]._id}
            type="top"
            text={burgerbun[0].name}
            price = {burgerbun[0].price}
            thumbnail = {burgerbun[0].image}
            isLocked={true}
            />}
          <ul style={{marginTop:"0", marginBottom:"0", listStyle:"none", display:"flex", flexDirection:"column", gap:"16px", paddingLeft:'0px', overflowY: "scroll", minHeight:"464px"}}>
          {burgerFilling.map( filling =>
            <BurgerConstructorBlock
            key = {filling._id}
            text={filling.name}
            price = {filling.price}
            thumbnail = {filling.image}
            isLocked={true}
            />
          )}
          </ul>
          {burgerbun.length>1 && <BurgerConstructorBlock
          key = {burgerbun[1]._id}
            type="bottom"
            text={burgerbun[1].name}
            price = {burgerbun[1].price}
            thumbnail = {burgerbun[1].image}
            isLocked={true}
            />}
          <BurgerTotalBlock/>
        </section>
      )
};
