export default function BurgerConstructor( { data }) {
    return (
        <div style={{ border:'solid red 1px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <ConstructorElement 
            style={{ border:'solid red 1px', height:"80px"}}
            type="top"
            isLocked={true}
            text="Краторная булка N-200i (верх)"
            price={200}

          />
          <ConstructorElement
            text="Краторная булка N-200i (верх)"
            price={50}

          />
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text="Краторная булка N-200i (низ)"
            price={200}

          />
        </div>
      )
};
