import styles from './BurgerConstructor.module.css';
import { Button, ConstructorElement, CurrencyIcon, DragIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import {useEffect, useState} from "react";

function ConstructorString({ 
  type,
  isLocked
  }) {
   return (
    <>
    <li  className={styles.ingredient__column}>
      <div>
            { !type && <DragIcon type="primary"/> }
      </div >
      <div className={styles.ingredient__element}>
      <ConstructorElement 
            type={type}
            isLocked={isLocked}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
          />
        </div>
    </li>
    </>
   )
}


function TotalBlock({ 
  }) {
   return (
    <div className="mr-4" style={{alignSelf:'end', display:'flex', alignItems:'center'}}>
      <div style={{display:'flex', alignItems:'center', gap:'10px'}}>
        <p className="text text_type_digits-default">1234567890</p>
        <CurrencyIcon type="primary"/>
      </div>
      <Button htmlType="button" type="primary" size="small" extraClass="ml-10" >
        <p className="text text_type_main-default">
          Оформить заказ
        </p>
      </Button>
    </div>
   )
}

export default function BurgerConstructor( { data }) {

  const burgerFilling = data.filter(element => element.type === 'main');
  console.log(burgerFilling)
    return (
        <section className="mt-20" style={{display: 'flex', flexDirection: 'column', gap: '16px',  height: '696px', width:'600px'}}>
            <ConstructorString
            type="top"
            isLocked={true}
            />
          <ul style={{marginTop:"0", marginBottom:"0", listStyle:"none", display:"flex", flexDirection:"column", gap:"16px", paddingLeft:'0px', overflowY: "scroll", minHeight:"464px"}}>
          {burgerFilling.map( filling =>
            <ConstructorString
            isLocked={true}
            />
          )}
          </ul>
          <ConstructorString
            type="bottom"
            isLocked={true}
            />
          <TotalBlock/>
        </section>
      )
};
