//import styles from './BurgerConstructor.module.css';
import { Button, ConstructorElement, CurrencyIcon, DragIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import {useEffect, useState} from "react";

function ConstructorString({ 
  type,
  isLocked
  }) {
   return (
    <li  style={{ border:"solid red 1px", display:"flex"}}>
      <div style={{ border:"solid red 1px"}}>
            { !type && <DragIcon type="primary"/> }
      </div>
      <ConstructorElement 
            type={type}
            isLocked={isLocked}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
          />
    </li>
   )
}


export default function BurgerConstructor( { data }) {
    return (
        <section style={{ border:"solid red 1px", display: 'flex', flexDirection: 'column', gap: '16px', overflowY: "scroll", height: '696px', width:'600px'}}>
          <ul style={{listStyle:"none", paddingLeft:'0px'}}>

            <ConstructorString/>

            <li>
            <ConstructorElement 
            type="top"
            isLocked={true}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
          />
          </li>
          <li className="mt-4">
          <ConstructorElement 
            isLocked={true}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
          />
          </li>
          </ul>
          <div style={{alignSelf:'end', display:'flex', alignItems:'center'}}>
            <div style={{display:'flex', alignItems:'center', gap:'10px'}}>
              <p className="text text_type_digits-default">1234567890</p>
              <CurrencyIcon type="primary"/>
            </div>
          <Button htmlType="button" type="primary" size="small" extraClass="ml-10" >
            <p className="text text_type_main-default">
              Оформить заказ
            </p>
          </Button>
          </div>
        </section>
      )
};
