import styles from './BurgerIngredients.module.css'
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import React from 'react'
import {Tab}  from "@ya.praktikum/react-developer-burger-ui-components"


const ingredientList = [
    {
       name: 'Булки',
       value: 'bun',
    },
    {
       name: 'Соусы',
       value: 'sauce',
    },
    {
       name: 'Начинки',
       value: 'main',
    }
 ]

function CardIngredient(group) {
    const ingridient = { 
        calories : '420',
        carbohydrates: '33',
        fat: '244',
        image: "https://code.s3.yandex.net/react/code/meat-02.png",
        image_large: "https://code.s3.yandex.net/react/code/meat-02-large.png",
        image_mobile: "https://code.s3.yandex.net/react/code/meat-02-mobile.png",
        name: "Мясо бессмертных моллюсков Protostomia",
        price : '1337',
        proteins : '433',
        type : "main",
        __v : '0',
        _id : "60d3b41abdacab0026a733c9"
        }
    return(
        <>
          <section style={{ display:"flex", flexDirection:"column",  width:"272px", height: "208px", border: "1px solid red", alignItems:'center'}}>
            <img src={group.image} style={{ width: "240px" , height: "120px" ,border: "1px solid red"}}/>
            <div className="mt-2 " style={{ display:"flex", alignItems:"center", gap:"9px"}}>
              <p className="text text_type_digits-default mt-2 " >{group.price}</p>
              <CurrencyIcon type="primary"  />
            </div>
            <p className='text text_type_main-small mt-2' style={{textAlign: "center" ,border: "1px solid red"}}>{group.name}</p>
          </section>
       </>
    )
}

 function Ingridients({ 
    ingredient,
    data
    }) {
    const ingredientGroup = data.filter(element => element.type === ingredient.value);
    console.log(ingredientGroup)
     return (
        <section className="mt-10">
          <li className="text text_type_main-medium">{ingredient.name}</li>
          <div className={styles.grid}>

          {ingredientGroup.map( group =>
            <CardIngredient 
            key={group.name}
            data={group}></CardIngredient>
        )}
          </div>
        </section> 
     )
 }


export default function BurgerIngredients({ data }) {

    const [current, setCurrent] = React.useState('one')


    return (
    <section className="mt-5 mr-10" style={{ border: "solid blue 1px", display: 'flex', flexDirection: 'column', overflowY: "scroll", height: '900px'}}>
        <h1 className="text text_type_main-large ml-10">Соберите бургер</h1>
        <div className="mt-5 ml-10" style={{ display: 'flex'}}>
        {ingredientList.map( tab =>
               <Tab
                  key={ tab.name }
                  value={ tab.value }
                  active={ current === tab.value }
                  onClick={ setCurrent }
               >{ tab.name }
               </Tab>
            )}
      </div>
      <div >
        <ul style={{ listStyle: 'none'}}>
        {ingredientList.map( component =>
            <Ingridients 
            key={component.name}
            ingredient={component}
            data={data}></Ingridients>
        )}
        </ul>
      </div>
      </section>
    )
}
