import styles from './BurgerIngredients.module.css'
import React from 'react'
import {Tab}  from "@ya.praktikum/react-developer-burger-ui-components"


const ingredientList = [
    {
       name: 'Булки',
       value: 'bun',
    },
    {
       name: 'Соусы',
       value: 'sauce',
    },
    {
       name: 'Начинки',
       value: 'main',
    }
 ]

function CardIngredient({data}) {

    //console.log(ingredientList[0].value)
    const buns = data.filter(element => element.type ===  ingredientList[0].value);
    console.log(buns[0])
    return(
       <p>{buns[0].name}</p>

    )
}

 function Ingridients({ ingredient , apiData}) {
     return (
        <>
        <li className="text text_type_main-medium">{ingredient.name}
        </li>
        <CardIngredient data = {apiData}></CardIngredient>
        </>
     )
 }




export default function BurgerIngredients({ data }) {

    const buns = data.filter(element => element.type === 'bun');
    const main = data.filter( el => el.type == 'main' );
    const sauce = data.filter( el => el.type == 'sauce' );

    //console.log(buns)
    const [current, setCurrent] = React.useState('one')


    return (
    <section className="mt-5 mr-10" style={{ display: 'flex', flexDirection: 'column'}}>
        <h1 className="text text_type_main-large ml-10">Соберите бургер</h1>
        <div className="mt-5 ml-10" style={{ display: 'flex'}}>
        {ingredientList.map( tab =>
               <Tab
                  key={ tab.name }
                  value={ tab.value }
                  active={ current === tab.value }
                  onClick={ setCurrent }
               >{ tab.name }
               </Tab>
            )}
      </div>
      <div>
        <ul style={{ listStyle: 'none'}}>
        {ingredientList.map( component =>
            <Ingridients 
            key={component.name}
            ingredient={component} apiData={data}></Ingridients>
        )}
        </ul>
      </div>
      </section>
    )
}
